INSERT INTO posts (user_id, usr_type_id, name, name_ES, slug, image, content, content_ES, enabled) VALUES (1, 1, 'Can Diabetes Cause a Metallic Taste in the Mouth?', '¿Puede la diabetes provocar un sabor metálico en la boca?', 'new2', 'https://mejorconsalud.as.com/wp-content/uploads/2015/10/Boca-con-sabor-met%C3%A1lico-500x334.jpg',
